import React, { useState } from "react";
import SortingContext from "./context/sortingContext";
import Navbar from "./components/navbar/nav";
import Visualizer from "../src/components/visualizer/visualizer.js";
import useSorting from "./scripts/hooks/useSorting";

const defaultValues = {
  algorithm: "Bubble Sort",
  arrSize: 200,
  sortSpeed: 200,
};

function App() {
  const { array, animation, config } = useSorting(defaultValues);

  return (
    <div className="App">
      <SortingContext.Provider value={{ array, animation, config }}>
        <Visualizer value={{ array }} />
        <Navbar />
      </SortingContext.Provider>
    </div>
  );
}

export default App;
