const mergeSort = (arr) => {
  if (arr == null) {
    return;
  }
  let i = 0;
  let j = 0;
  let k = 0;

  if (arr.length > 1) {
    i = 0;
    j = 0;
    k = 0;

    const mid = parseInt(arr.length / 2);

    // Split left part
    const left = Array(mid).fill(0);
    for (i = 0; i < mid; i++) {
      left[i] = arr[i];
    }

    // Split right part
    const right = Array(arr.length - mid).fill(0);
    for (i = mid; i < arr.length; i++) {
      right[i - mid] = arr[i];
    }
    mergeSort(left);
    mergeSort(right);

    i = 0;
    j = 0;
    k = 0;

    // Merge left and right arrays
    while (i < left.length && j < right.length) {
      if (left[i] < right[j]) {
        arr[k] = left[i];
        i++;
      } else {
        arr[k] = right[j];
        j++;
      }
      k++;
    }
    // Collect remaining elements
    while (i < left.length) {
      arr[k] = left[i];
      i++;
      k++;
    }
    while (j < right.length) {
      arr[k] = right[j];
      j++;
      k++;
    }
  }
};

// Driver program to test above functions.
export const printArr = () => {
  var arr = [2, 1, 6, 16, 53, 9];
  var i = 0;
  document.write("Given array is<br/>");

  for (i = 0; i < arr.length; i++) document.write(arr[i] + " ");

  mergeSort(arr);

  document.write("<br/><br/>");
  document.write("Sorted array is<br/>");

  for (i = 0; i < arr.length; i++) document.write(arr[i] + " ");
};

export default mergeSort;
