import React, { useState } from "react";
import Navbar from "../navbar/nav";
import { getIntFromTo } from "../../algorithms/util";

import "../../css/visualizer.css";

const Visualizer = () => {
  // Test Array before I initialize an array on start-up
  let testArr = [];
  for (let k = 0; k < 200; k++) {
    testArr.push(getIntFromTo(10, 1000));
  }

  // Declare state variable array
  const [array, setArray] = useState(testArr);

  // Declare Handlers
  const resetArray = () => {
    setArray((prev) => {
      for (let i = 0; i < 200; i++) {
        prev.push(getIntFromTo(10, 1000));
        return prev;
      }
    });
  };

  return (
    <>
      <div>
        <Navbar resetHandler={resetArray} />
      </div>
      <div className="array-container">
        {array.map((value, idx) => (
          <div
            className="array-bar"
            key={idx}
            style={{ height: `${value / 1.5}px` }}
          ></div>
        ))}
      </div>
    </>
  );
};

export default Visualizer;
