/***************************************************************************************************************************************
 Name: Matthew Kouchi
 File: quickSort.js
 Date: JUN_6_2021
 
 Abstract: 
***************************************************************************************************************************************/

import { swap } from "./util";

export function quickSort(arr, start, end) {
  if (start >= end) {
    return;
  }

  let index = partion(arr, start, end);
  doQuick(arr, start, index - 1);
  doQuick(arr, index + 1, end);

  return arr;
}

function partion(arr, startIdx, endIdx) {
  let pivotValue = arr[endIdx];
  let pivotIdx = startIdx;
  for (let i = startIdx; i < endIdx; i++) {
    if (arr[i] < pivotValue) {
      swap(arr, i, pivotIdx);
      pivotIdx++;
    }
  }
  swap(arr, pivotIdx, endIdx);
  return pivotIdx;
}
