import { swap } from "./util";

const selectionSort = (unsortedArray) => {
  let arr = [...unsortedArray];
  let arrSnapshot = [[...arr]];
  let len = arr.length;
  let temp, min;

  for (let i = 0; i < len; i++) {
    min = i;
    for (let j = i + 1; j < len; j++) {
      arr[j] = { ...arr[j], highlighted: true };
      arrSnapshot.push([...arr]);
      arr[j] = { ...arr[j], highlighted: false };
      if (arr[j].value < arr[min].value) {
        arr[min] = { ...arr[min], highlighted: false };
        min = j;
        arr[min] = { ...arr[min], highlighted: true };
      }
    }
    swap(arr, i, j);
    arrSnapshot.push([...arr]);
  }

  return arrSnapshot;
};

export default selectionSort;
