let globalSnapshot = [];

const mergeSort = (arr) => {
  globalSnapshot = [[...arr]];
  mergeSortHelper(arr);
  return globalSnapshot;
};

const mergeSortHelper = (arr) => {
  if (arr == null) {
    return;
  }

  if (arr.length > 1) {
    let i = 0;
    let j = 0;
    let k = 0;

    const mid = parseInt(arr.length / 2);

    // Split left part
    const left = Array(mid).fill(0);
    for (i = 0; i < mid; i++) {
      left[i] = arr[i];
      globalSnapshot.push([...arr]);
    }

    // Split right part
    const right = Array(arr.length - mid).fill(0);
    for (i = mid; i < arr.length; i++) {
      right[i - mid] = arr[i];
    }
    mergeSortHelper(left);
    mergeSortHelper(right);

    i = 0;
    j = 0;
    k = 0;

    // Merge left and right arrays
    while (i < left.length && j < right.length) {
      if (left[i] < right[j]) {
        arr[k] = left[i];
        i++;
      } else {
        arr[k] = right[j];
        j++;
      }
      k++;
    }
    // Collect remaining elements
    while (i < left.length) {
      arr[k] = left[i];
      i++;
      k++;
    }
    while (j < right.length) {
      arr[k] = right[j];
      j++;
      k++;
    }
  }
};

export default mergeSort;
