const insertionSort = (unsortedArray) => {
  // Declare Local Variables
  let arr = [...unsortedArray];
  let arrSnapshot = [[...arr]]; // Each Idx contains an array snapshot
  const len = arr.length;
  for (let i = 1; i < len; i++) {
    let current = arr[i];
    let j = i - 1;
    while (j >= 0 && arr[j] > current) {
      arr[j + 1] = arr[j];
      j--;
    }
    arr[j + 1] = current;
  }
};
