import styled from "styled-components";

const Bar = styled.div`
  margin: 0px 2px;
  transition: background-color 0.2s;
  background-color: #2979ff;
  height: ${({ height }) => (height ? height : "50%")};
  width: ${({ width }) => (width ? width : "10%")};
`;
