/***************************************************************************************************************************************
 Name: Matthew Kouchi
 File: visualizer.js
 Date: JUN_4_2021
 
 Abstract: Contains the visualizer where the array container resides. The visualizer uses the Sorting Context of the project to map
 the entire current array to the screen. The map function renders the arrays index's and gives it a height based on the index's value
 which is assigned when the array is generated.
***************************************************************************************************************************************/

import "../../css/visualizer.css";
import React, { useContext } from "react";
import Bar from "../../css/styled/bar";

import SortingContext from "../../context/sortingContext";
import ThemeContext from "../../context/colorContext";

const Visualizer = () => {
  const { array } = useContext(SortingContext);
  const { colors } = useContext(ThemeContext);

  const renderArray = () => {
    array.currentArray.map(({ value, highlighted }) => {
      return (
        <Bar
          key={value}
          highlighted={highlighted}
          height={`${(value / array.currentArray.length) * 90}%`}
          width={`${1000 / array.currentArray.length}%`}
          baseColor={colors.baseColor}
          highlightedColor={colors.highlightedColor}
          sortedColor={colors.sortedColor}
        />
      );
    });
  };

  return <div className="array-container">{renderArray()}</div>;
};

export default Visualizer;
