import { useState } from "react";

const useColor = () => {
  const [highlightColor, setHighlightColor] = useState("ef9a9a");
  const [sortedColor, setSortedColor] = useState("aed581");
  const [baseColor, setBaseColor] = useState("2979ff");
  const [selectColor, setSelectColor] = useState("4d8fff");

  return {
    colors: { highlightColor, sortedColor, baseColor, selectColor },
    update: { setHighlightColor, setSortedColor, setBaseColor, setSelectColor },
  };
};

export default useColor;
