import styled from "styled-components";

const Bar = styled.div`
  width: 5px;
  margin: 0px 2px;
  background-color: #2979ff;
  transition: background-color 0.2s;
  height: 200px
  width: ${({ width }) => (width ? width : "10%")};
  background: ${({ baseColor }) => baseColor},
    ${({ highlighted, highlightedColor }) =>
      highlighted &&
      `background: ${highlightedColor};
      border: none;
    `};
`;

export default Bar;
