import styled from "styled-components";

const Bar = styled.div`
  width: 5px;
  margin: 0px 2px;
  background-color: #2979ff;
  transition: background-color 0.2s;
  height: ${({ height }) => (height ? height : "50%")};
`;

export default Bar;
