/***************************************************************************************************************************************
 Name: Matthew Kouchi
 File: quickSort.js
 Date: JUN_6_2021
 
 Abstract: Quick sort uses a pivot and partitions the array to have indexes with lesser values on one side and with greater values on
 the opposite side. Then afer setting a new pivot by ... , the array is partitioned again. This is done by recursivley calling 
 quick sort on both sides of the pivot (both sides essentially act as individual arrays) This goes on until the starting idx is equal to 
 or greater than the end idx, meaning each partition is just a single element.  
***************************************************************************************************************************************/

import { swap } from "./util";

let curArr = [];

const quickSort = (unsortedArray) => {
  // Declarations
  let initArray = [...unsortedArray];
  let arrSnapshot = [[...initArray]];
  const start = 0;
  const end = initArray.length - 1;

  if (start >= end) {
    return;
  }

  let temp = partition(initArray, start, end, arrSnapshot);
  let index = temp[temp.length];
  temp.pop();
  arrSnapshot = [...temp];

  quickSort(curArr, start, index - 1, arrSnapshot);
  quickSort(curArr, index + 1, end, arrSnapshot);

  return arrSnapshot;
};

const partition = (arr, startIdx, endIdx, snap) => {
  const pivotValue = arr[endIdx].value;
  let pivotIdx = startIdx;
  for (let i = startIdx; i < endIdx; i++) {
    if (arr[i].value < pivotValue) {
      swap(arr, i, pivotIdx);
      arr[pivotIdx] = { ...arr[pivotIdx], highlighted: true };
      snap.push([...arr]);
      arr[pivotIdx] = { ...arr[pivotIdx], highlighted: false };
      pivotIdx++;
    }
  }

  swap(arr, pivotIdx, endIdx);
  arr[endIdx] = { ...arr[endIdx], highlighted: true };
  snap.push([...arr]);
  arr[endIdx] = { ...arr[endIdx], highlighted: false };

  curArr = snap[snap.length - 1];
  const arrSnapshot = snap.concat(pivotIdx);

  return arrSnapshot;
};

export default quickSort;
