import React, { useState, useEffect } from "react";
import Navbar from "../navbar/nav";

import "../../css/visualizer.css";

const Visualizer = () => {
  // Declare state variable array
  const [array, setArray] = useState([]);

  useEffect(() => {
    const arr = resetArray();
    <div>
      {array.map((value, idx) => (
        <div
          className="array-bar"
          key={idx}
          style={{ height: `${value / 1.5}px` }}
        ></div>
      ))}
    </div>;
    console.log("array reset");
  }, []);

  // Declare Handlers
  const resetArray = () => {
    setArray((newArr) => {
      for (let i = 0; i < 200; i++) {
        newArr.push(getIntFromTo(10, 1000));
      }
      return newArr;
    });
  };

  return (
    <>
      <div>
        <Navbar resetHandler={resetArray} />
      </div>
      <div className="array-container">
        {array.map((value, idx) => (
          <div
            className="array-bar"
            key={idx}
            style={{ height: `${value / 1.5}px` }}
          ></div>
        ))}
      </div>
    </>
  );
};

// Get random int from a to b
function getIntFromTo(min, max) {
  return Math.floor(Math.random() * (max - min + 1) + min);
}

export default Visualizer;
