import React, { useContext } from "react";
import InputButton from "../../css/styled/button";
import Nav from "../../css/styled/nav";

import "../../css/navbar.css";
import SortingContext from "../../context/sortingContext";

// Navbar Component
const Navbar = () => {
  const { animation, settings } = useContext(SortingContext);

  const selectAlgorithm = () => {
    const updateAlgorithm = (e) => {
      settings.setAlgorithm(e.target.value);
      console.log(settings.algorithm);
    };
    return (
      <Nav>
        <>
          <InputButton value="Heap Sort" onClick={updateAlgorithm} />
          <InputButton
            value="Bubble Sort"
            onClick={updateAlgorithm().then(() => {
              console.log(settings.algorithm);
            })}
          />
          <InputButton value="Quick Sort" onClick={updateAlgorithm} />
          <InputButton value="Merge Sort" onClick={updateAlgorithm} />
          <InputButton value="Insertion Sort" onClick={updateAlgorithm} />
        </>
      </Nav>
    );
  };

  const selectSpeed = () => {
    return (
      <label>
        Sorting Speed
        <select
          value={settings.sortSpeed}
          onChange={(e) => settings.setSortSpeed(e.target.value)}
        >
          <option value={350}>Slow</option>
          <option value={200}>Normal</option>
          <option value={100}>Fast</option>
          <option value={75}>Super Fast</option>
        </select>
      </label>
    );
  };

  const selectSize = () => {
    return (
      <label>
        Size of Array
        <select
          value={settings.arrSize}
          onChange={(e) => settings.setArrSize(e.target.value)}
        >
          <option value={10}>10</option>
          <option value={25}>25</option>
          <option value={50}>50</option>
          <option value={100}>100</option>
          <option value={200}>200</option>
        </select>
      </label>
    );
  };

  const playAnimation = () => {
    return (
      <>
        <h1>Play</h1>
        <button onClick={() => animation.startAnimating()} />
      </>
    );
  };

  return (
    <form>
      {selectAlgorithm()}
      {selectSpeed()}
      {selectSize()}
      {playAnimation()}
    </form>
  );
};

export default Navbar;
