import React, { useContext } from "react";
import InputButton from "../../css/styled/button";
import Nav from "../../css/styled/bar";

import "../../css/nav.css";
import SortingContext from "../../context/sortingContext";

// Navbar Component
const Navbar = () => {
  const { config } = useContext(SortingContext);

  const selectAlgorithm = () => {
    const updateAlgorithm = (e) => {
      config.setAlgorithm(e.target.value);
    };
    <Nav value={config.algorithm}>
      <>
        <InputButton value="Heap Sort" onClick={updateAlgorithm}>
          Heap Sort
        </InputButton>
        <InputButton value="Bubble Sort" onClick={updateAlgorithm}>
          Bubble Sort
        </InputButton>
        <InputButton value="Quick Sort" onClick={updateAlgorithm}>
          Quick Sort
        </InputButton>
        <InputButton value="Merge Sort" onClick={updateAlgorithm}>
          Merge Sort
        </InputButton>
        <InputButton value="Insertion Sort" onClick={updateAlgorithm}>
          Insertion Sort
        </InputButton>
      </>
    </Nav>;
  };

  const selectSpeed = () => {
    <label>
      Sorting Speed
      <select
        value={config.sortSpeed}
        onChange={(e) => config.setSortingInterval(e.target.value)}
      >
        <option value={300}>Slow</option>
        <option value={200}>Normal</option>
        <option value={100}>Fast</option>
        <option value={85}>Faster</option>
        <option value={50}>Laggy</option>
      </select>
    </label>;
  };

  const selectSize = () => {
    <label>
      Size of Array
      <select
        value={config.arrSize}
        onChange={(e) => config.setArraySize(e.target.value)}
      >
        <option value={10}>10</option>
        <option value={25}>25</option>
        <option value={50}>50</option>
        <option value={100}>100</option>
      </select>
    </label>;
  };

  return (
    <form>
      {selectAlgorithm()}
      {selectSpeed()}
      {selectSize()}
    </form>
  );
};

export default Navbar;
