import styled from "styled-components";

const handleColorType = (highlighted, sorted) => {
  if (highlighted === false) {
    switch (sorted) {
      case true:
        return "green";
      default:
        return "turquoise";
    }
  } else {
    if (sorted === false) return "red";
  }
};

const Bar = styled.div`
  width: 5px;
  margin: 0px 2px;
  height: ${({ height }) => (height ? height : "50%")};
  width: ${({ width }) => (width ? width : "10%")};
  background-color: ${({ highlighted, sorted }) =>
    handleColorType(highlighted, sorted)}
  
}`;

export default Bar;

