/***************************************************************************************************************************************
 Name: Matthew Kouchi
 File: quickSort.js
 Date: JUN_6_2021
 
 Abstract: Quick sort uses a pivot and partitions the array to have indexes with lesser values on one side and with greater values on
 the opposite side. Then afer setting a new pivot by ... , the array is partitioned again. This is done by recursivley calling 
 quick sort on both sides of the pivot (both sides essentially act as individual arrays) This goes on until the starting idx is equal to 
 or greater than the end idx, meaning each partition is just a single element.  
***************************************************************************************************************************************/

import { swap } from "./util";

const quickSort = (unsortedArray) => {
  // Declarations
  let arr = [...unsortedArray];
  let arrSnapshot = [[...arr]];
  const startIdx = 0;
  const endIdx = arr.length - 1;

  // Call Sorting Algorithm with Initial Conditions
  arrSnapshot = doQuick(arr, arrSnapshot, startIdx, endIdx);

  return arrSnapshot;
};

const doQuick = (arr, snap, start, end) => {
  // Check if array is fully partitioned
  if (start >= end) {
    return;
  }

  // Declare a pivot idx
  let index = partition(arr, snap, start, end);
  // Recursivley quick sort either side of the pivot idx
  doQuick(arr, snap, start, index - 1);
  doQuick(arr, snap, index + 1, end);

  return arrSnapshot;
};

const partition = (arr, snap, start, end) => {
  let pivotValue = arr[end];
  let pivotIdx = start;
  for (let i = start; i < end; i++) {
    if (arr[i] < pivotValue) {
      swap(arr, i, pivotIdx);
      snap.push([...arr]);
      pivotIdx++;
    }
  }
  swap(arr, pivotIdx, end);
  snap.push([...arr]);

  // Reset Props to Initials
  snap[snap.length - 1].forEach((idx) => {
    idx.highlighted = false;
  });

  return pivotIdx;
};

export default quickSort;
