import styled from "styled-components";

const handleColorType = (props) => {
  switch (props) {
    case props.highlighted:
      return "red";
    case props.sorted:
      return "green";
    default:
      return "turqoise";
  }
};

const Bar = styled.div`
  width: 5px;
  margin: 0px 2px;
  height: ${({ height }) => (height ? height : "50%")};
  width: ${({ width }) => (width ? width : "10%")};
  background-color: ${({ props }) => handleColorType(props)}
  
}`;

export default Bar;

