let globalSnapshot = [];
let globalArr = [];

const mergeSort = (arr) => {
  globalArr = arr;
  globalSnapshot = [[...arr]];
  mergeSortHelper(globalArr);
  return globalSnapshot;
};

const mergeSortHelper = (arr) => {
  if (arr === null) {
    return;
  }
  let i = 0;
  let j = 0;
  let k = 0;

  if (arr.length > 1) {
    const mid = parseInt(arr.length / 2);

    // Split left part
    //const left = arr.slice(0, mid);
    const left = new Array(mid).fill(0);
    for (i = 0; i < mid; i++) {
      left[i] = arr[i];
    }

    // Split right part
    //const right = arr.slice(mid, arr.length);
    const right = new Array(arr.length - mid).fill(0);
    for (i = mid; i < arr.length; i++) {
      right[i - mid] = arr[i];
    }

    mergeSortHelper(left);
    mergeSortHelper(right);

    i = 0;
    j = 0;

    // Merge left and right arrays
    while (i < left.length && j < right.length) {
      if (left[i].value < right[j].value) {
        arr[k] = left[i];
        globalArr[k] = arr[k];
        globalSnapshot.push([...globalArr]);
        i++;
      } else {
        arr[k] = right[j];
        globalArr[k] = arr[k];
        globalSnapshot.push([...globalArr]);
        j++;
      }
      k++;
    }
    // Collect remaining elements
    while (i < left.length) {
      arr[k] = left[i];
      globalArr[k] = arr[k];
      globalSnapshot.push([...globalArr]);
      i++;
      k++;
    }
    while (j < right.length) {
      arr[k] = right[j];
      globalArr[k] = arr[k];
      globalSnapshot.push([...globalArr]);
      j++;
      k++;
    }
  }
};

export default mergeSort;
